const Instagram = () => {

    const [instaTokenResult, setInstaTokenResult] = useState([]);

    const loadInstaSetting = () => {
      getInstaToken().then((data) => {

          console.log(data);
          setInstaTokenResult(data.data);

      });
    };

    useEffect(() => {
      loadInstaSetting();
    }, []);

    console.log(instaTokenResult.token);
